import React, { useContext, useEffect, useState } from 'react'
import Product from '../Products/Product'
import { Cart } from '../../components/Context/Context'
import './Card.css'

const Card = ({prod}) => {

  const {cart,setCart}=useContext(Cart)


  const[model,setModel]=useState(false)
  const[tempdata,setTempdata]=useState([])

  const getData=(image,title,price)=>{
    let tempData=[image,title,price]
    setTempdata(item=> [1, ...tempData ])
    return setModel(true);
  }



  const [total,setTotal]=useState()

  useEffect(()=>{
    setTotal(cart.reduce((acc,curr)=>acc+Number(curr.price),0))
  },[cart])


  return (
    <div className='container'>
      <div className="row">
      <span style={{fontSize:30}}>My Cart</span>
      <br />
      <span style={{fontSize:30}}>Total:{total} AZN</span>
      <div className='productContainer'>
        {
          cart.map(prod=>(
            <div className="wrap-table-shopping-cart bgwhite w-100">
              <table className="table-shopping-cart">
              <tbody>
		    <tr>
		      <td className="w-25 tablecard">
			      <img src={prod.image} className="img-fluid img-thumbnail imgs" alt=""/>
		      </td>
		      <td><p className='text-center'>{prod.title}</p></td>
		      <td><p className='text-end'>{prod.price} AZN</p></td>
          <td>
            <div className="btn text-end">
            {cart.includes(prod)?(
        <button 
        className='remove' 
        onClick={()=>{
            setCart(cart.filter((c)=>c._id !==prod._id))
        }}
        >Remove From Card
        </button>
        ):(
        <button 
        className='add' 
        onClick={()=>{
            setCart([...cart,prod])
        }}
        >Add To Card
        </button>


        )}
          </div>
        </td>
		    </tr>
		  </tbody>
            </table>
            </div>
          ))
        }
      </div> 
      </div>
    </div>
  )
}

export default Card
