  return (

<div className="container">
  <div className="row">
  <div className='products'>
        <div className="box">
        <img src={prod.image} alt={prod.title} />
        <div className='productDesc'>
            <p style={{fontWeight:700}}>{prod.title}</p>
            <p> {prod.price} AZN</p>
        </div>
        {cart.includes(prod)?(
        <button 
        className='remove' 
        onClick={()=>{
            setCart(cart.filter((c)=>c._id !==prod._id))
        }}
        >Remove From Cart
        </button>
        ):(
        <button 
        className='add' 
        onClick={()=>{
            setCart([...cart,prod])
        }}
        >Add To Cart
        </button>
        )}

        </div>

    </div>
  </div>
</div>
  )
}

export default Product
