              <NavLink to={`/updateproducts/${product_arr[id]}`} onClick={async() => {
                  await axios.put(`http://localhost:5000/card/${product_arr[id]}`)
                  .then(res => res.data)
               }} className='btn btn1 ms-1 btnn'>Update</NavLink>
