import axios from 'axios'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import SideBarDrop from '../../components/SideBarDropdown/SideBarDrop'
import './AddAdress.css'

const AddAdress = () => {
    const history=useNavigate()

    const [inputs,setInputs]=useState({
        adress:'',
        phone:'',
        mail:''
    }) 

    const handleChange=(e)=>{
        setInputs((prevState)=>({
            ...prevState,
            [e.target.name] : e.target.value
        }))
    }


    const handleSubmit=(e)=>{
        e.preventDefault();
        sendRequest().then(()=>history('/admin'));
    }

    const sendRequest=async()=>{
        await axios.post('http://localhost:5000/adress',{
            image: String(inputs.adress),
            message:String(inputs.phone),
            user:String(inputs.mail)
        }).then(res=>res.data)
    }

  return (
      <div>
    <div className='row justify-content-center'>
      <div className="col-lg-2">
        <SideBarDrop/>
      </div>
      <div className="container addadress col-lg-4">
          <form onSubmit={handleSubmit} className="content">
              <h1 id="say-hello">Add Adress !</h1>
              <div>
              <div className="image">
                  <label for="adress">Adress</label><br />
                  <input value={inputs.adress} onChange={handleChange} name='adress' className="fld" placeholder="Enter adress" id="adress" />
              </div>

              <div className="title">
                  <label for="phone">Phone</label><br />
                  <input  value={inputs.phone} onChange={handleChange} name='phone' class="fld" placeholder="Enter phone" id="phone" />
              </div>

              <div className="user">
                  <label for="mail">Mail</label><br />
                  <input value={inputs.mail} onChange={handleChange} name='mail' class="fld" placeholder="Enter mail" id="mail" />
              </div>
              </div>
              <button className="btn upd" type="submit">ADD</button>
          </form>
  </div>
  </div>
  </div>
  )
}

export default AddAdress
