import React, { useEffect, useState } from 'react'
import './Cart.css'
import {useDispatch,useSelector} from 'react-redux'
import {addtocart,deletefrombasket} from '../../actions'
import {v4 as uuid} from 'react-uuid'
import axios from 'axios'

const Cart = () => {

    const dispatch=useDispatch()

    const deletefromcart=(value)=>{
        dispatch(deletefrombasket(value))
    }

    const addtobasket=()=>{
        dispatch(addtocart({id:uuidv4(),product}))
    }

    const[product,setProducts]=useState([])

    useEffect(() => {
        axios.get('http://localhost:5000/card')
          .then(res => setProducts(res.data.cards)) 
          .catch(error => console.log(error))
      },[])

  return (
    <div>
        {product&&product.map((productss,id)=>(
        <div>
            <div key={id} className="cartitem">
                <img className='img-fluid' src={productss.image} alt="" />
                <a href="/">{productss.title}</a>
                <span>{productss.price} AZN</span>
        </div>
            <div className="cartbuttons">
                <button onClick={()=>addtobasket}>Add To Basket</button>
                <button onClick={()=>deletefromcart(id)}>Delete</button>
            </div>
        </div>
         ))}
    </div>
  )
}

export default Cart
