  //   return setModel(true);
  // }

  // const[product,setProducts]=useState([])

  // useEffect(() => {
  //   axios.get('http://localhost:5000/card')
  //     .then(res => setProducts(res.data.cards)) 
  //     .catch(error => console.log(error))
  // },[])

  return (

<div className='products col-lg-3 row'>
        <img src={prod.image} alt={prod.title} />
        <div className='productDesc'>
            <span style={{fontWeight:700}}>{prod.title}</span>
            <span> {prod.price} AZN</span>
        </div>
        {cart.includes(prod)?(
        <button 
        className='remove' 
        onClick={()=>{
            setCart(cart.filter((c)=>c._id !==prod._id))
        }}
        >Remove From Cart
        </button>
        ):(
        <button 
        className='add' 
        onClick={()=>{
            setCart([...cart,prod])
        }}
        >Add To Cart
        </button>
        )}


    </div>
  )
}

export default Product
