import React, { useContext, useEffect, useState } from 'react'
import { Cart } from '../../components/Context/Context'
import './Card.css'

const Card = ({prod}) => {

  const {cart,setCart}=useContext(Cart)

  const [total,setTotal]=useState()

  useEffect(()=>{
    setTotal(cart.reduce((acc,curr)=>acc+Number(curr.price),0))
  },[cart])


  return (
       <div className='container'>
      <div className="row">
      <div className='productContainers col-lg-8 align-items-center left'>
        {
          cart.map(prod=>(
            <div className="bgwhite w-100">
              <table className="table table-striped">
         <tbody>
            <tr>
              <div className="divtd">
              <td className="tablecard text-center">
                <img src={prod.image} className="img-fluid img-thumbnail imgs" alt=""/>
              </td>
              <td className='w-25' ><p className='text-center mb-0'>{prod.title}</p></td>
              <td className='w-25' ><p className='text-center mb-0' >{prod.price} AZN</p></td>
              <td className='w-25 text-center'>
                {cart.includes(prod)?(
            <button 
            className='remove' 
            onClick={()=>{
                setCart(cart.filter((c)=>c._id !==prod._id))
            }}
            >Remove From Card
            </button>
            ):(
            <button 
            className='add' 
            onClick={()=>{
                setCart([...cart,prod])
            }}
            >Add To Card
            </button>

            )}
            </td>
              </div>
            </tr>
		     </tbody>
            </table>
            </div>
          ))
        }
      </div> 
     <div className="carttotals right col-lg-4">
     <div>
      <span style={{fontSize:30}}>My Cart</span>
      <br />
      <span style={{fontSize:30}}>Total:{total} AZN</span>
      </div>
     </div>
      </div>
    </div>
  )
}

export default Card
