import React from 'react'
import { items } from './MenuItems'
import './Menu.css'
import { NavLink } from 'react-router-dom'

const Menu = ({isOpen,onChange}) => {
  return (
    <div className={`menubar ${isOpen && "open"}`}>
      <span className='material-icons btn-close'
      onClick={()=>onChange(false)}></span>
        {items.map((item,index)=> <NavLink to={item.itemLink} key={index}>
            {item.itemText}
        </NavLink>)}
    </div>
  )
}

export default Menu
