import axios from 'axios'
import React, { useContext, useEffect, useReducer, useState }  from 'react'
import { createContext } from 'react'
import { cartReducer } from './Reducers'
import { productReducer } from './Reducers'

const Cart=createContext()

const BasketContext = ({children}) => {

  const[product,setProducts]=useState([])

  useEffect(() => {
    axios.get('http://localhost:5000/card')
      .then(res => setProducts(res.data.cards)) 
      .catch(error => console.log(error))
  },[])

  const products=[...product].map(()=>({
    image:product.image(),
    title:product.title(),
    price:product.price()
  }))

  const[state,dispatch]=useReducer(cartReducer,{
    products:products,
    cart:[]
});
const[productState,productDispatch]=useReducer(productReducer,{
    searchQuery:"", 
})

  return (
    <Cart.Provider value={{state,dispatch,productState,productDispatch}}>{children}</Cart.Provider>
  )
}

export default BasketContext


export const CartState=()=>{
    return useContext(Cart)
}
