import axios from 'axios'
import React, { useContext, useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'
import './Product.css'
import Modal from '../Modal/Modal'
import { Cart } from '../../components/Context/Context'


const Product = ({prod}) => {
  const {cart,setCart}=useContext(Cart)


  const[model,setModel]=useState(false)
  const[tempdata,setTempdata]=useState([])

  const getData=(image,title,price)=>{
    let tempData=[image,title,price]
    setTempdata(item=> [1, ...tempData ])
    return setModel(true);
  }

  return (
    <> 
   <div className='products'>
        <img className='imgprd' src={prod.image} alt={prod.title} />
        <div className='productDesc'>
            <p className='col-lg-12 title' style={{fontWeight:700}}>{prod.title}</p>
            <p className='col-lg-12'>{prod.price} AZN</p>
        </div>
        <div className="btns">
        <div className="btn">
              <NavLink to="/" onClick={()=>getData(prod.image, prod.title, prod.price)}>Quick View</NavLink>
            </div>
        {/* {cart.includes(prod)?(
        <button 
        className='remove' 
        onClick={()=>{
            setCart(cart.filter((c)=>c._id !==prod._id))
        }}
        >Remove From Card
        </button>
        ):(
        <button 
        className='add' 
        onClick={()=>{
            setCart([...cart,prod])
        }}
        >Add To Card
        </button>

        )} */}
        </div>
        {
    model===true ? <Modal image={tempdata[1]} title={tempdata[2]} price={tempdata[3]} prod={prod} hide={()=>setModel(false)}/> :" "
  }
  {/* <div className='loadmorediv'>
    <NavLink className='loadmore' to='/'>LOAD MORE</NavLink>
  </div>  */}
    </div>

 </>
  )
}

export default Product
